class MenuFragment(var position : Int) : Fragment() {

    val data = arrayListOf(
        ProductItem(
            "klinskoe",
            "https://102922.selcdn.ru/nomenclature_images/e464c693-4a57-11e5-80c1-d8d385655247/8bd70139-41ce-44a5-ba16-41f617533d78.jpg"
        ),
        ProductItem(
            "pizza",
            "data:image/jpeg;base64,"
        ),
        ProductItem(
            "sushi",
            "https://www.tokyo-city.ru/goods/1sushi_sake_gurme.jpg"
        ),
        ProductItem(
            "pasta",
            "data:image/jpeg;base64,"
        ),
        ProductItem(
            "cola",
            "data:image/jpeg;base64,"
        )
    )

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        var view = inflater.inflate(R.layout.fragment_menu, container, false)
        var viewPager2 = view.findViewById<ViewPager2>(R.id.pager)
        var tabLayout = view.findViewById<TabLayout>(R.id.tab_layout)



        var tabs = listOf("first", "second", "third", "forth", "fifth", "sixth", "seventh")
        var dd = mapOf(tabs[0] to data, tabs[1] to data, tabs[2] to data, tabs[3] to data, tabs[4] to data, tabs[5] to data, tabs[6] to data)

        var dat = listOf(data, data, data, data, data, data, data)

//        viewPager2.reduceDragSensitivity()
//        viewPager2.orientation = ViewPager2.ORIENTATION_VERTICAL
        viewPager2.adapter = MenuAdapter(dd.values.toList())

        viewPager2.setCurrentItem(position, false)


        TabLayoutMediator(tabLayout, viewPager2) { tab, position ->
            //tab.text = tabs[position]
            tab.text = dd.keys.elementAt(position)
            tab.customView
        }.attach()

        return view
    }

    fun ViewPager2.reduceDragSensitivity() {
        val recyclerViewField = ViewPager2::class.java.getDeclaredField("mRecyclerView")
        recyclerViewField.isAccessible = true
        val recyclerView = recyclerViewField.get(this) as RecyclerView

        val touchSlopField = RecyclerView::class.java.getDeclaredField("mTouchSlop")
        touchSlopField.isAccessible = true
        val touchSlop = touchSlopField.get(recyclerView) as Int
        touchSlopField.set(recyclerView, touchSlop*8)       // "8" was obtained experimentally
    }

}
